function getMaxZIndex(){let maxZIndex=-Infinity;for(let element of document.querySelectorAll('*')){let zIndex=window.getComputedStyle(element).zIndex;if(zIndex!=='auto'){maxZIndex=Math.max(maxZIndex,parseInt(zIndex))}}
return maxZIndex===-Infinity?null:maxZIndex}
let MAlertQueue=[];let MAlertCounterID=0;let MAlertCurrentZIndex=0;function MAlert(MAlertContent,action=null,title=''){const lock=action==='lock';const realAction=(!lock&&typeof action==='function')?action:null;MAlertCounterID++;const alertId=`MAlert${MAlertCounterID}`;MAlertCurrentZIndex=getMaxZIndex()+1;const ranid='scroll-'+Math.random().toString(36).substr(2,9);if(!document.getElementById(ranid)){const scrollbarCSS=`
.mAlertModal div::-webkit-scrollbar{width:8px}.mAlertModal div::-webkit-scrollbar-track{background:#fff0;border-radius:4px;margin:4px 0}.mAlertModal div::-webkit-scrollbar-thumb{background:gray;border-radius:4px;transition:background 0.3s ease}.mAlertModal div::-webkit-scrollbar-thumb:hover{background:gray}.mAlertModal div::-webkit-scrollbar-thumb:active{background:gray}
        `;const styleElement=document.createElement('style');styleElement.id=ranid;styleElement.textContent=scrollbarCSS;document.head.appendChild(styleElement)}
const alertHTML=`
        <div id="${alertId}" style="margin:0; width:100%; height:100%; top:0; left:0; position: fixed; z-index: ${MAlertCurrentZIndex}; font-family: system-ui; background: rgba(0,0,0,0.3); opacity: 0; transition: opacity 100ms ease-out;">
            <!-- Container principal do modal -->
            <div class="mAlertModal" style="font-size: 14px; box-shadow: 0 0 9999px 9999px #2121217a; background: #f7f7f7f7; max-width: 500px; width: 90%; max-height: 80vh; padding: 20px; margin: 0; position: absolute; top: 50%; left: 50%; transform: translate(-50%, -50%) scale(1); text-align: center; color: black !important; border-radius: 4px; transition: transform 200ms ease-out; display: flex; flex-direction: column;">
                
                <!-- Título do alerta (exibido apenas se fornecido) -->
                <h2 style="margin: 0; font-weight: 600; font-size: 1.4em; color: #743ad5; display: ${title && title.trim() !== '' ? 'block' : 'none'}; flex-shrink: 0;">${title || ''}</h2>

                <!-- Área de conteúdo com scroll automático -->
                <div class="malert-content" style="text-align: center; margin: 10px 0; color: black !important; overflow-y: auto; max-height: calc(80vh - 100px); flex-grow: 1; padding-right: 4px;">
                    <div>${MAlertContent}</div>
                </div>
                
                <!-- Botão de fechar (oculto quando lock=true) -->
                <button class="mAlertClose" style="width: 30px; height: 30px; border: none; font-size: 20px; position: absolute; top: 5px; color: #575757; right: 9px; cursor: pointer; background: transparent; display: ${lock ? 'none' : 'inline-block'}; z-index: 1;">×</button>
                
            </div>
        </div>
    `;document.body.insertAdjacentHTML('afterend',alertHTML);const alertElement=document.getElementById(alertId);const modalElement=alertElement.querySelector('.mAlertModal');setTimeout(()=>{alertElement.style.opacity='1';modalElement.style.transform='translate(-50%, -50%) scale(1)'},10);const closeButton=alertElement.querySelector('.mAlertClose');if(closeButton){closeButton.addEventListener('click',function(){alertElement.style.opacity='0';modalElement.style.transform='translate(-50%, -50%) scale(0.7)';setTimeout(()=>{if(alertElement.parentNode){alertElement.parentNode.removeChild(alertElement)}},200);MAlertQueue=MAlertQueue.filter(alert=>alert.id!==alertId);if(typeof action==='function'){action()}})}
MAlertQueue.push({id:alertId,action:realAction,lock:lock})}
function removeAllMAlerts(){MAlertQueue.forEach(alert=>{const alertElement=document.getElementById(alert.id);if(alertElement){const modalElement=alertElement.querySelector('.mAlertModal');alertElement.style.opacity='0';if(modalElement){modalElement.style.transform='translate(-50%, -50%) scale(0.7)'}
setTimeout(()=>{if(alertElement.parentNode){alertElement.parentNode.removeChild(alertElement)}},200)}});MAlertQueue=[]}
